var expect = require('chai').expect;
var TimeSheet = require('../');

describe('#TimeSheetCore', function() {

  it('should export object class', function() {
    expect(TimeSheet).to.be.an.instanceOf(Object);
  });

  it('should be able to create new instance', function() {
    expect(new TimeSheet).to.be.an.instanceOf(TimeSheet);
  });

  it('should export selected libraries', function() {
    var timesheet = new TimeSheet('appId', 'appSecret');
    expect(timesheet).to.be.an.instanceOf(TimeSheet);
    expect(timesheet.auth).to.be.an.instanceOf(Object);
    expect(timesheet.auth.getAuthorizeUrl).to.be.an.instanceOf(Function); // Double check
  });
})
